import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { getStorage, ref, listAll, getDownloadURL } from 'firebase/storage';
import { app } from "./firebase";

function SendToBackend() {
  const [imageList, setImageList] = useState([]);
  const [inputArray, setInputArray] = useState([]);
  const [responseMessage, setResponseMessage] = useState('');

  // Function to send array to backend
  const sendArrayToBackend = async () => {
    try {
      console.log("Array: ", imageList);
      const response = await axios.post('http://localhost:5000/api/data', { array: await imageList });
      setResponseMessage(response.data.success ? 'Array sent successfully' : 'Error sending array');
    } catch (error) {
      console.error('Error sending array:', error);
      setResponseMessage('Error sending array');
    }
  };

  const getURLs = () => {
    // Reference to the Firebase Storage bucket
    const storage = getStorage(app);
    const storageRef = ref(storage);

    // List all items in the Storage bucket
    listAll(storageRef)
      .then((result) => {
        const images = result.items.map((item) => {
          // Get the download URL for each image
          console.log("Item: ", item);
          return getDownloadURL(item);
        });

        // Update the state with the list of image URLs
        Promise.all(images).then((urls) => {
          console.log("URL: ", urls);
          setImageList(urls);
        });
      })
      .catch((error) => {
        console.error('Error listing images:', error);
      });
  }

  useEffect(() => {
    getURLs();
  // useEffect to send array to backend on page load
    sendArrayToBackend(); 
  }, []);

  return (
    <div>
      <h1>React-Node Array Communication</h1>
      <div>
        <label>
          Enter array elements (comma-separated):
          <input
            type="text"
            value={inputArray.join(',')}
            onChange={(e) => setInputArray(e.target.value.split(','))}
          />
        </label>
      </div>
      <button onClick={sendArrayToBackend}>Send Array to Backend</button>
      <div>
        <p>Response from Backend: {responseMessage}</p>
      </div>
    </div>
  );
}

export default SendToBackend;
