/* eslint-disable jsx-a11y/alt-text */
import React, { useEffect, useState } from 'react';
import Spinner from 'react-bootstrap/Spinner';
import axios from 'axios';
import { getStorage, ref, listAll, getDownloadURL } from 'firebase/storage';
import { app } from "./firebase";
import { getApp } from "firebase/app";
// import './App.css';

const AppData = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [dataList, setDataList] = useState([]);
  const [imageList, setImageList] = useState([]);
  const [inputArray, setInputArray] = useState([]);
  const [responseMessage, setResponseMessage] = useState('');

  
  // Function to send array to backend
  const sendArrayToBackend = async (urls) => {
    try {
      console.log("Array: ", urls);
      // Change url with url of server
      const response = await axios.post('http://localhost:5000/api/data', { array: urls });
      setResponseMessage(response.data.success ? 'Array sent successfully' : 'Error sending array');
      fetchData();
    } catch (error) {
      // console.error('Error sending array:', error);
      setResponseMessage('Error sending array');
    }
  };

  const getURLs_data = async () => {
    // Reference to the Firebase Storage bucket
    const storage = getStorage(app);
    const storageRef = ref(storage, 'text_data');

    // List all data in the Storage bucket
    listAll(storageRef)
      .then((result) => {
        const images = result.items.map((item) => {
          // Get the download URL for each image
          console.log("Item Data: ", item);
          return getDownloadURL(item);
        });

        // Update the state with the list of image URLs
        Promise.all(images).then((urls) => {
          // console.log("URL: ", urls);
          setDataList(urls);
          // Call sendArrayToBackend after updating imageList
          sendArrayToBackend(urls);
        });
      })
      .catch((error) => {
        console.error('Error listing images:', error);
      });

      getURLs_image();
  }

  const getURLs_image = async () => {
    // Reference to the Firebase Storage bucket
    const storage = getStorage(app);
    const storageRef = ref(storage, 'images/default_user_id');
    // const storageRef = ref(storage, 'gs://vim-recyclers-aa737.appspot.com');
    // const imageRef = ref(storage, 'images/default_user_id/');

    // List all data in the Storage bucket
    console.log("Storage Ref. = ", storageRef);
    listAll(storageRef)
      .then((result) => {
        const images = result.items.map(async(item) => {
          const imagePath = result.fullPath;
          const url = await storageRef.ref(imagePath).getDownloadURL;
          // Get the download URL for each image
          console.log("Item Images: ", image);
          return getDownloadURL(item);
        });

    // // List all data in the Storage bucket
    // listAll(imageRef)
    //   .then((result) => {
    //     const images = result.items.map((item) => {
    //       // Get the download URL for each image
    //       console.log("Item: ", item);
    //       return getDownloadURL(item);
    //     });

        // Update the state with the list of image URLs
        Promise.all(images).then((urls) => {
          // console.log("URL: ", urls);
          setImageList(urls);
          // Call sendArrayToBackend after updating imageList
          sendArrayToBackend(urls);
        });
      })
      .catch((error) => {
        console.error('Error listing images:', error);
      });
  }

  const fetchData = async () => {
    try {
      // Change url with url of server
      const response = await axios.get('http://localhost:5000/api/data');
      const res_data = response.data;
      if(imageList.length !== 0){
        const new_data = data.map((item, index) => ({
          ...item,
          image_url: res_data[index]
        }));
        console.log("New Data: ", new_data);
        setData(new_data);
      }
      else{
        setData(res_data);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };


  useEffect(() => {
    getURLs_data();
  }, []);

  return (
<div>
{loading ? (
  <Spinner animation="border" role="status">
      <span className="visually-hidden">Loading...</span>
    </Spinner>
      ) : (
        <table>
        <thead>
            <tr>
              <th>S.No.</th>
              <th>Material</th>
              <th>Grade</th>
              {/* <th>Thickness</th> */}
              <th>Type</th>
              {/* <th>Comments</th> */}
              <th>Weight in pounds</th>
              <th>Number of Bales</th>
              <th>IsRebale</th>
              <th>HasPalate</th>
              <th>Image</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <tr key={index}>
                <td className="firstCol">{index+1}</td>
                <td>{item.material}</td>
                <td>{item.grade}</td>
                {/* <td>{item.thickness}</td> */}
                <td>{item.selectedType}</td>
                {/* <td>{item.comments}</td> */}
                <td>{item.weight}</td>
                <td>{item.numBales}</td>
                <td>{item.isRebale ? 'Yes' : 'No'}</td>
                <td>{item.hasPalate ? 'Yes' : 'No'}</td>
                <td><img src={item.image_url} />
                <button 
                onClick={()=>{ window.open(item.image_url, '_blank');
                }} className='view' >View</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AppData;
